import React from "react";

const LandingPage = () => {
  return (
    <div className="bg-gradient-to-b from-white from-40% via-white to-green-200 h-screen flex flex-col items-center justify-center p-4">
      {/* Option 1: Text shadow approach */}
      <h1 className="text-6xl md:text-8xl lg:text-9xl font-extrabold bg-gradient-to-r from-green-500 via-yellow-400 to-green-600 bg-clip-text text-transparent drop-shadow-[0_2px_2px_rgba(0,0,0,0.3)] mb-6">
        AI-Powered Streak Counter
      </h1>
      
      {/* Alternative heading with darker colors for better visibility */}
      <h2 className="text-5xl md:text-7xl font-bold text-green-700 mt-8">
        Track Your Progress
      </h2>
    </div>
  );
};

export default LandingPage;
