import React, { useEffect } from "react";

const LandingPage = () => {
  useEffect(() => {
    // Load Google's authentication script
    const script = document.createElement("script");
    script.src = "https://accounts.google.com/gsi/client";
    script.async = true;
    script.defer = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  // Handle Google Sign-In
  const handleGoogleSignIn = (response) => {
    // Send the ID token to your backend
    console.log("Google Sign-In successful:", response);
    // Here you would typically send this token to your backend
  };

  return (
    <div className="bg-gradient-to-b from-yellow-50 from-40% via-amber-50 to-yellow-100 h-screen">
      <div className="p-20">
        <h1 className="text-7xl font-extrabold bg-gradient-to-r from-amber-200 via-yellow-400 to-amber-300 bg-clip-text text-transparent drop-shadow-sm mb-6">
          AI-Powered Streak Counter
        </h1>
        <h1 className="text-9xl font-extrabold bg-gradient-to-r from-amber-200 via-yellow-400 to-amber-300 bg-clip-text text-transparent drop-shadow-sm mb-6">
          ⚡Streak⚡
        </h1>

        {/* Google Sign-In Button */}
        <div className="mt-8 mb-8">
          <div
            id="g_id_onload"
            data-client_id="YOUR_GOOGLE_CLIENT_ID.apps.googleusercontent.com"
            data-context="signin"
            data-ux_mode="popup"
            data-callback="handleCredentialResponse"
            data-auto_prompt="false"
          ></div>

          <div
            className="g_id_signin"
            data-type="standard"
            data-shape="rectangular"
            data-theme="outline"
            data-text="signin_with"
            data-size="large"
            data-logo_alignment="left"
          ></div>
        </div>

    
        <button className="bg-white text-gray-800 font-bold py-2 px-6 rounded shadow-md hover:bg-gray-100 flex items-center mb-8">
          <svg className="h-6 w-6 mr-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 48 48">
            <path fill="#FFC107" d="M43.611,20.083H42V20H24v8h11.303c-1.649,4.657-6.08,8-11.303,8c-6.627,0-12-5.373-12-12s5.373-12,12-12c3.059,0,5.842,1.154,7.961,3.039l5.657-5.657C34.046,6.053,29.268,4,24,4C12.955,4,4,12.955,4,24s8.955,20,20,20s20-8.955,20-20C44,22.659,43.862,21.35,43.611,20.083z" />
            <path fill="#FF3D00" d="M6.306,14.691l6.571,4.819C14.655,15.108,18.961,12,24,12c3.059,0,5.842,1.154,7.961,3.039l5.657-5.657C34.046,6.053,29.268,4,24,4C16.318,4,9.656,8.337,6.306,14.691z" />
            <path fill="#4CAF50" d="M24,44c5.166,0,9.86-1.977,13.409-5.192l-6.19-5.238C29.211,35.091,26.715,36,24,36c-5.202,0-9.619-3.317-11.283-7.946l-6.522,5.025C9.505,39.556,16.227,44,24,44z" />
            <path fill="#1976D2" d="M43.611,20.083H42V20H24v8h11.303c-0.792,2.237-2.231,4.166-4.087,5.571c0.001-0.001,0.002-0.001,0.003-0.002l6.19,5.238C36.971,39.205,44,34,44,24C44,22.659,43.862,21.35,43.611,20.083z" />
          </svg>
          Sign in with Google
        </button>

        <p className="text-xl bg-gradient-to-r from-amber-400 via-yellow-900 to-amber-400 bg-clip-text text-transparent">
          Click to streak. Get inspired. Stay healthy. See your progress.
        </p>
      </div>
    </div>
  );
};

// This function needs to be globally accessible for the Google Sign-In callback
window.handleCredentialResponse = (response) => {
  // Handle the Google Sign-In response
  console.log("Encoded JWT ID token: " + response.credential);
  // You would typically send this token to your backend
};

export default LandingPage;
