import React, { useEffect } from "react";

const LandingPage = () => {
  useEffect(() => {
    // Load Google's authentication script
    const script = document.createElement("script");
    script.src = "https://accounts.google.com/gsi/client";
    script.async = true;
    script.defer = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  // Handle Google Sign-In
  const handleGoogleSignIn = (response) => {
    // Send the ID token to your backend
    console.log("Google Sign-In successful:", response);
    // Here you would typically send this token to your backend
  };

  return (
    <div className="bg-gradient-to-b from-yellow-50 from-40% via-amber-50 to-yellow-100 m-20 h-screen">
      <div className="p-20 text-center">
        <h1 className="text-7xl font-extrabold bg-gradient-to-r from-amber-200 via-yellow-400 to-amber-300 bg-clip-text text-transparent drop-shadow-sm mb-6">
          AI-Powered Streak Counter
        </h1>
        <h1 className="text-9xl font-extrabold bg-gradient-to-r from-amber-200 via-yellow-400 to-amber-300 bg-clip-text text-transparent drop-shadow-sm mb-6">
          ⚡Streak⚡
        </h1>


        <p className="text-xl bg-gradient-to-r from-amber-400 via-yellow-900 to-amber-400 bg-clip-text text-transparent">
          Click to streak. Get inspired. Stay healthy. See your progress.
        </p>
      </div>
    </div>
  );
};


export default LandingPage;
